currentStatusList.forEach((currentStatusInfo, index) => {

    const lastStatus = statusList.find(service => service.serviceName === currentStatusInfo.serviceName);

    if ( NOT_NOTIFY_SERVICE_NAME_LIST.indexOf(lastStatus.serviceName) > -1 ) {
        updateStatusList[index] = currentStatusInfo;
