    }

    if( currentStatusInfo.status.toLocaleUpperCase() === "ONLINE" ) {
        if( lastStatus.status === "OFFLINE" ) {
            // node.error('online')
            backToOnlineService.push( currentStatusInfo.serviceName );
        }

