        updateStatusList[index]['lastUpdated'] = Date.now()

    } else {

        if( lastStatus.status === "ONLINE" ) {
            // node.error('last status ONLINE')
            updateStatusList[index] = currentStatusInfo;
            updateStatusList[index]['lastUpdated'] = Date.now()
            // updateStatusList[index]['status'] = currentStatusInfo['status'];
            // updateStatusList[index]['lastUpdated'] = currentStatusInfo['lastUpdated'];

            if( NOT_NOTIFY_SERVICE_NAME_LIST.indexOf(lastStatus.serviceName) === -1 ) {
                // node.error('offline 1');
                offlineService.push( lastStatus.serviceName );
            }

        } else {
            // node.error('last status !== ONLINE')
            if( lastStatus.hasOwnProperty('lastUpdated') && lastStatus.lastUpdated ) {
                // node.error(`lastStatus.hasOwnProperty('lastUpdated') && lastStatus.lastUpdated `)
                if( (currentStatusInfo.lastUpdated - lastStatus.lastUpdated) > NOTIFICATION_TIME_INTERVAL ) {
                    // node.error(`currentStatusInfo.lastUpdated - lastStatus.lastUpdated) > NOTIFICATION_TIME_INTERVAL`)
                    updateStatusList[index] = currentStatusInfo;
                    updateStatusList[index]['lastUpdated'] = Date.now()
                    // updateStatusList[index]['status'] = currentStatusInfo['status'];
                    // updateStatusList[index]['lastUpdated'] = currentStatusInfo['lastUpdated'];

                    if( NOT_NOTIFY_SERVICE_NAME_LIST.indexOf(lastStatus.serviceName) === -1 ) {
                        // node.error('offline 2');
                        offlineService.push( lastStatus.serviceName );
                    }

                } else {
                    updateStatusList[index] = lastStatus;
                }
            } else {
                updateStatusList[index] = currentStatusInfo;
                updateStatusList[index]['lastUpdated'] = Date.now()
                // updateStatusList[index]['status'] = currentStatusInfo['status'];
                // updateStatusList[index]['lastUpdated'] = currentStatusInfo['lastUpdated'];
            }

        }

    }
});

