function calculateFee(feeType, packageFeeInfo) {
    let returnObj = {};
    if( packageFeeInfo.hasOwnProperty(feeType) ) {
        returnObj = packageFeeInfo[feeType];

        let charge = returnObj['charge'];
        let amountCharge = 0;
        if( returnObj['chargeType'] === "PERCENT" ) {
            amountCharge = Number( parseFloat((amount * charge) / 100).toFixed(2) );
        } else {
            amountCharge = Number( parseFloat(amount - charge) );
        }


        returnObj['serviceType'] = ( feeType === "service" ) ? "SERVICE_FEE" : "PAYMENT_GATEWAY_FEE" ;
        returnObj['amountCharge'] = amountCharge;
    }

    return returnObj;
}

