function calculateNextCircleDate(packageObj, startDate) {
    let billingType = packageObj.billingInfo.billingType; 
    let billingDate = packageObj.billingInfo.billingDate;

    let nextStartDate = startDate;
    if( billingType.length > 0 ) {
        switch( billingType ) {
            case "WEEKLY":
                nextStartDate = startDate + ( ( (6 - new Date(startDate).getDay()) + billingDate) * 24 * 60 * 60 * 1000);
                break;
            case "MONTHLY":
                let nextMonth = new Date(startDate).getMonth() + 1;
                let year = new Date(startDate).getFullYear();
                if( nextMonth > 11 ) {
                    nextMonth = 0;
                    year = year + 1;
                }
                nextStartDate = new Date(year, nextMonth, billingDate, new Date(startDate).getHours(), new Date(startDate).getMinutes(), new Date(startDate).getSeconds()).getTime();
                break;
            case "YEARLY":
                nextStartDate = new Date(new Date(startDate).getFullYear() + 1, 0, billingDate, new Date(startDate).getHours(), new Date(startDate).getMinutes(), new Date(startDate).getSeconds()).getTime();
                break;
            case "FIX":
                nextStartDate = startDate + ( packageObj.activeDays * 24 * 60 * 60 * 1000);
                break;
            default:
                nextStartDate = startDate;
        }
    } else {
        nextStartDate = startDate + ( packageObj.activeDays * 24 * 60 * 60 * 1000);
    }

    return nextStartDate;
}

function calculateChargePrice(currentDate, activeDate, newPackageObj, currentPackageObj) {
    let billingType = newPackageObj.billingInfo.billingType; 
    // let billingDate = newPackageObj.billingInfo.billingDate;

    let newMemberPrice = 0;
    let currentMemberPrice = currentPackageObj && currentPackageObj.hasOwnProperty('memberPrice') ? currentPackageObj.memberPrice : 0 ;
    if(newPackageObj.memberPrice > currentMemberPrice) {
        newMemberPrice = newPackageObj.memberPrice - currentMemberPrice;
    }

    let chargePrice = 0;
    if( billingType.length > 0 ) {
        let lastDayOfMonth;
        let lastDayOfYear;
        switch( billingType ) {
            case "WEEKLY":
                chargePrice = (newMemberPrice / 7) * ( (activeDate - currentDate) / 86400 );
                break;
            case "MONTHLY":
                lastDayOfMonth = new Date(new Date(currentDate).getFullYear(), new Date(currentDate).getMonth() + 1, 0).getDate();
                chargePrice = (newMemberPrice / lastDayOfMonth) * ( (activeDate - currentDate) / 86400000 );               
                break;
            case "YEARLY":
                lastDayOfYear = new Date(new Date(currentDate).getFullYear() + 1, 0, 0).getDate();
                chargePrice = (newMemberPrice / lastDayOfYear) * ( (activeDate - currentDate) / 86400 );
                break;
            default:
                chargePrice = newMemberPrice * ( (activeDate - currentDate) / 86400 );
        }
    } else {
        chargePrice = newMemberPrice;
    }

    return chargePrice;
}


if( newPackageObj.isSubscribePackage ) {
    if( isActiveNow === "false" || isActiveNow === false ) {
        // activeDate = calculateActiveDate(newPackageObj, currentDate);
        activeDate = calculateNextCircleDate(currentPackageObj, currentDate);
        expiryDate = calculateNextCircleDate(newPackageObj, activeDate);
        chargePrice = newPackageObj.memberPrice;
    } else {
        activeDate = currentDate;
        expiryDate = calculateNextCircleDate(newPackageObj, activeDate);
        chargePrice = calculateChargePrice(activeDate, expiryDate, newPackageObj, currentPackageObj);
    }
    // expiryDate = calculateExpiryDate(newPackageObj.billingInfo.billingType, newPackageObj.billingInfo.billingDate, currentDate, activeDate);
} else {
    chargePrice = newPackageObj.memberPrice;
    if( isActiveNow === "false" || isActiveNow === false ) {
        // activeDate = this.calculateActiveDate(newPackageObj, currentDate);
        activeDate = calculateNextCircleDate(currentPackageObj, currentDate);
    } else {
        activeDate = currentDate;
    }
    expiryDate = activeDate + ( newPackageObj.activeDays * 24 * 60 * 60 * 1000);
}


