const ORDER_ID = msg.orderID;

const checkMerchantDefined1 = (ORDER_ID, responseObj) => {
    return responseObj.hasOwnProperty('merchantDefined1') && ( (responseObj['merchantDefined1'] !== null && responseObj['merchantDefined1'] == ORDER_ID) || responseObj['merchantDefined1'] === null );
}

const processResponse = (ORDER_ID, responseObj) => {
    let returnObj = {
        "method": "",
        "status": ""
    };

    let paymentMethod = "QR_CODE";
    let paymentStatus = "PENDING";
    if( responseObj.hasOwnProperty('cardNo') ) {
        paymentMethod = "CREDIT_CARD";
    }

    if( responseObj.hasOwnProperty('status') ) {
        const responseStatus = responseObj['status'].toUpperCase();

        if( checkMerchantDefined1(ORDER_ID, responseObj) ) {
            if( responseStatus === "S" ) {
                paymentStatus = "SUCCESS";
            } else if ( responseStatus === "G" ) {
                paymentStatus = "PENDING";
            } else if ( responseStatus === "D" ) {
                paymentStatus = "FAIL";
            } else if ( responseStatus === "A" ) {
                if( paymentMethod === "CREDIT_CARD" ) {
                    paymentStatus = "SUCCESS";
                } else {
                    paymentStatus = "PENDING";
                }
            }

            returnObj['gb_response'] = responseObj;
        }

    }
    returnObj['method'] = paymentMethod;
    returnObj['status'] = paymentStatus;

    return returnObj;
}

