        if( gbResponse.hasOwnProperty('merchantDefined1') && gbResponse['merchantDefined1'] === ORDER_ID ) {
            response = processResponse(ORDER_ID, gbResponse);
            break;
        }
    }
} else {
    node.error('Found single response.');
