node.error(voteArr)

for(let i=0;i < voteArr.length;i++){
    if(voteArr[i].length > 0){
        let voteObj = JSON.parse(voteArr[i])
        if(voteObj.number == voteNumber){
            hadVote = true
            index = i
        }
    }
}

if(hadVote){
    let voteObj = JSON.parse(voteArr[index])
    let voteStr = JSON.stringify({"number": voteObj.number,"value": voteObj.value+1})
    voteArr.splice(index,1)
    voteArr.push(voteStr)
}
else{
    let voteStr = JSON.stringify({"number": voteNumber,"value": 1});
    voteArr.push(voteStr)
}
let voteArrStr = voteArr.join("\n")
